package tttPD;

// helper class for ai, ment for easy 2d return value
public class BoardPosition {
	public int x = 0;
	public int y = 0;

	public BoardPosition() {
		// TODO Auto-generated constructor stub
	}

	public BoardPosition(int x, int y) {
		// TODO Auto-generated constructor stub
		this.x = x;
		this.y = y;
	}

	public int getX() {
		return this.x;
	}

	public int getY() {
		return this.y;
	}
}
